var Login = function() {
    return {
        //main function to initiate the module
        init: function() {
            // init background slide images
            $('.login-bg').backstretch([
                // "/public/images/bg1.jpg",
                "/public/images/bg2.jpg",
                "/public/images/bg3.jpg"
                ], {
                  fade: 1000,
                  duration: 8000
                }
            );
        }
    };
}();

jQuery(document).ready(function() {
    Login.init();
});